sourcesContent:
/*
  Authors : initappz (Rahul Jograna)
  Website : https://initappz.com/
  App Name : ionic 5 groceryee app
  Created : 10-Sep-2020
  This App Template Source code is licensed as per the
  terms found in the Website https://initappz.com/license
  Copyright and Good Faith Purchasers © 2020-present initappz.
*/
import { ChangeDetectorRef, Component, OnInit, ViewChild } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';
import { ModalDirective } from 'angular-bootstrap-md';
import { ApiService } from 'src/app/services/api.service';
import { CartService } from 'src/app/services/cart.service';
import { UtilService } from 'src/app/services/util.service';
import * as moment from 'moment';
import { sortBy } from 'lodash';
import { OwlOptions } from 'ngx-owl-carousel-o';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  @ViewChild('basicModal') public basicModal: ModalDirective;
  dummyCates = Array(30);
  categories: any[] = [];

  dummyBanners: any[] = [];
  banners: any[] = [];

  bottomDummy: any[] = [];
  bottomBanners: any[] = [];

  betweenDummy: any[] = [];
  betweenBanners: any[] = [];

  dummyTopProducts: any[] = [];
  topProducts: any[] = [];

  products: any[] = [];
  dummyProducts: any[] = [];

  haveStores: boolean;

  dummyStores: any[] = [];
  stores: any[] = [];

  dummyOffers: any[] = [];
  offers: any[] = [];

  bottomcategory: any[] = [];
  dummyBottomCates = Array(2);
  haveCity: boolean;

  myCarouselOptions = {
    loop: false,
    margin: 20,
    nav: true,
    dots: false,
    autoplay: true,
    responsive: {
      0: {
        items: 2,
      },
      600: {
        items: 3
      },
      800: {
        items: 3
      },
      1000: {
        items: 5
      }
    }
  }

  myCategoryOptions = {
    loop: false,
    margin: 20,
    nav: true,
    dots: false,
    autoplay: true,
    responsive: {
      0: {
        items: 3,
      },
      600: {
        items: 3
      },
      800: {
        items: 3
      },
      1000: {
        items: 6
      }
    }
  };

  mystoreOptions = {
    loop: false,
    margin: 20,
    nav: true,
    dots: false,
    autoplay: true,
    responsive: {
      0: {
        items: 2,
      },
      600: {
        items: 3
      },
      800: {
        items: 3
      },
      1000: {
        items: 6
      }
    }
  }
  constructor(
    private router: Router,
    public api: ApiService,
    public util: UtilService,
    private chMod: ChangeDetectorRef,
    public cart: CartService) {


    this.dummyCates = Array(30);
    this.dummyBanners = Array(30);
    this.bottomDummy = Array(30);
    this.betweenDummy = Array(30);
    this.dummyTopProducts = Array(30);
    this.dummyOffers = Array(30);
    this.offers = [];
    this.categories = [];
    this.banners = [];
    this.bottomBanners = [];
    this.betweenBanners = [];
    this.topProducts = [];
    this.products = [];
    this.bottomcategory = [];
    this.dummyBottomCates = Array(2);
    const city = localStorage.getItem('city');
    console.log('city', localStorage.getItem('city'));
    if (city && city != null && city !== 'null') {
      this.haveCity = true;
      this.getInit();
    } else {
      console.log('no city found...');
    }
    this.util.subscribeCity().subscribe((data: any) => {
      this.dummyCates = Array(30);
      this.dummyBanners = Array(30);
      this.bottomDummy = Array(30);
      this.betweenDummy = Array(30);
      this.dummyTopProducts = Array(30);
      this.dummyOffers = Array(30);
      this.offers = [];
      this.categories = [];
      this.stores = [];
      this.banners = [];
      this.bottomBanners = [];
      this.betweenBanners = [];
      this.topProducts = [];
      this.products = [];
      this.bottomcategory = [];
      this.dummyBottomCates = Array(2);
      this.getInit();
    });
    setTimeout(() => {
      const acceptedCookies = localStorage.getItem('acceptedCookies');
      if (acceptedCookies && acceptedCookies != null && acceptedCookies !== 'null') {
      } else {
        this.basicModal.show();
      }
    }, 1000);
  }

  getBanners() {
    this.dummyBanners = Array(30);
    this.api.get('banners').then((data: any) => {
      console.log(data);
      this.dummyBanners = [];
      this.betweenDummy = [];
      this.bottomDummy = [];
      this.bottomBanners = [];
      this.betweenBanners = [];
      this.banners = [];
      if (data && data.status === 200 && data.data && data.data.length) {
        data.data.forEach(element => {
          if (element && element.status === '1') {
            if (element.position === '0') {
              this.banners.push(element);
            } else if (element.position === '1') {
              this.bottomBanners.push(element);
            } else {
              this.betweenBanners.push(element);
            }
          }
        });

        console.log('top', this.banners);
        console.log('bottom', this.bottomBanners);
        console.log('between', this.betweenBanners);
        this.chMod.detectChanges();
        console.log('detect changes');
      }
    }, error => {
      console.log(error);
      this.dummyBanners = [];
    });
  }
  ngOnInit(): void {
  }

  getInit() {
    this.dummyCates = Array(30);
    this.dummyBanners = Array(30);
    this.bottomDummy = Array(30);
    this.betweenDummy = Array(30);
    this.dummyTopProducts = Array(30);
    this.categories = [];
    this.banners = [];
    this.bottomBanners = [];
    this.betweenBanners = [];
    this.topProducts = [];
    this.products = [];
    const param = {
      id: localStorage.getItem('city')
    };
    this.api.post('stores/getByCity', param).then((stores: any) => {
      console.log('stores by city', stores);
      this.stores = [];
      if (stores && stores.status === 200 && stores.data && stores.data.length) {
        console.log('city found');
        this.stores = stores.data;

        this.stores.forEach(async (element) => {
